import React from 'react';


class ChildA extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            childData : "This is child data sending to parent component"
        }
    }
    componentDidMount(){
        this.sendDataToParent()
    }
    sendDataToParent = () =>{
        this.props.childData(this.state.childData)
    }
    render(){
        return (
            <div>
                {this.props.parent}
            </div>
        )
    };
};

export default ChildA
