import React from 'react';
import ChildA from '../components/childA'

class ParentA extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            receivedChildData : ""
        }
    }
    componentDidMount(){
        this.receivedChildDataa()
    }
    receivedChildDataa = (data) =>{
        this.setState({
            receivedChildData : data
        })
    }
    render() {
        return (
            <div>
                <ChildA childDataToParent={this.receivedChildDataa}/>
                <p>{this.state.receivedChildData}</p>
            </div>
        )
    };
};
export default ParentA
