import React from 'react';
import ChildA from '../components/childA'

class ParentA extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            parentData: "",
            childData:""
        }
    }
    handleParentData = () => {
        this.setState({
            parentData: "This is parent data."
        })
    }
    componentDidMount() {
        this.handleParentData()
    }
    fromChildData =(data)=>{
        this.setState({
            childData: data
        })
    }
    render() {
        return (
            <div>
                <p>This is parent component.</p>
                <ChildA parentData={this.state.parentData} childData={this.fromChildData}/>
                {this.state.childData}
            </div>
        )
    };
};
export default ParentA
