import React from 'react';
import ChildA from '../components/childA'

class ParentA extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            parentData: "",
            childData : ""
        }
        console.log(props)
    }
    componentDidMount(){
        this.sendToChild()
    }
    sendToChild = ()=>{
        this.setState({
            parentData : "This is parent data in child"
        })
    }
    render() {
        return (
            <div>
                <p>This is parent component.</p>
                <ChildA parent={this.state.parentData}/>
                {this.state.childData}
            </div>
        )
    };
};
export default ParentA
